import FilteredMovies from "../Components/FilteredMovies";
import RandomMovies from "../Components/RandomMovies";
import SearchBar from "../Components/SearchBar";
import { useState, useEffect } from "react";
import { options } from "../Utils/options";

function Movies({ searchValue, setSearchValue, movies }) {
  const [highlyRated, setHighlyRated] = useState([]);
  const handleSearch = () => {
    console.log("Searching for:", searchValue);
  };
  let randomPage = Math.floor(Math.random() * 500);

  useEffect(() => {
    fetch(
      `https://api.themoviedb.org/3/movie/top_rated?language=en-US&page=${randomPage}`,
      options
    )
      .then((response) => response.json())
      .then((data) => {
        setHighlyRated(data.results);
        console.log("moives with top rated");
      })
      .catch((err) => console.error(err));
  }, []);

  return (
    <div>
      <div>
        <div id='home-container'>
          <div id='home-name'>
            <h1>Movies</h1>
          </div>
          <SearchBar
            searchValue={searchValue}
            onChangeValue={setSearchValue}
            onSearch={handleSearch}
          />
        </div>
        {searchValue ? (
          <FilteredMovies searchValue={searchValue} />
        ) : (
          <RandomMovies listOfMovies={highlyRated} />
        )}
      </div>
    </div>
  );
}

export default Movies;
