import SummaryOfAddedMovies from "../Components/SummaryOfAddedMovies";
import { useEffect, useState } from "react";

import MoviesAddedToAccount from "../Components/MoviesAddedToAccount";

function Account() {
  const [movies, setMovies] = useState([]);
  const [count, setCount] = useState(0);

  useEffect(() => {
    fetch("http://localhost:3000/movies")
      .then((response) => response.json())
      .then((data) => {
        console.log(movies);
        setMovies(data);
        setCount(data.length);
      });
  }, []);

  function handleDeletion() {
    console.log("deleted from Account");

    fetch(`http://localhost:3000/movies/${movies.id}`, {
      method: "DELETE",
    })
      .then((response) => response.ok)
      .then((movie) => deleteMovie(movie.id));
  }

  function deleteMovie(id) {
    const deleteMovie = movies.filter((movie) => movie.id !== id);
    setMovies(deleteMovie);
  }

  return (
    <div>
      <div id='account-container'>
        <div id='home-name'>
          <h1>Account</h1>
        </div>
        <SummaryOfAddedMovies count={count} />
      </div>
      <MoviesAddedToAccount listOfMovies={movies} deleteMovie={deleteMovie} />
    </div>
  );
}

export default Account;
