import FilteredMovies from "../Components/FilteredMovies";
import RandomMovies from "../Components/RandomMovies";
import SearchBar from "../Components/SearchBar";
import { useState, useEffect } from "react";
import { options } from "../Utils/options";

function Movies({ page, setPage, searchValue, setSearchValue, addMovie }) {
  const handleSearch = () => {
    console.log("Searching for:", searchValue);
  };

  useEffect(() => {
    fetch(
      `https://api.themoviedb.org/3/movie/top_rated?language=en-US&page=${page}`,
      options
    )
      .then((response) => response.json())
      .then((data) => {
        setHighlyRated((prevMovies) => [...prevMovies, ...data.results]);
      })
      .catch((err) => console.error(err));
  }, [page]);

  return (
    <div>
      <div>
        <div id='home-container'>
          <div id='home-name'>
            <h1>Movies</h1>
          </div>
          <SearchBar
            searchValue={searchValue}
            onChangeValue={setSearchValue}
            onSearch={handleSearch}
          />
        </div>
        {searchValue ? (
          <FilteredMovies searchValue={searchValue} />
        ) : (
          <RandomMovies addMovie={addMovie} />
        )}
      </div>
    </div>
  );
}

export default Movies;
