import { useEffect } from "react";

function ImagesPlace() {
  const imgPlace = document.querySelector(".grid-container");
  let valueSpan = 0;

  const [movies, setMovies] = useState([]);

  useEffect(() => {
    const options = {
      method: "GET",
      headers: {
        accept: "application/json",
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiJmNDdkZTBlMGY4ZGZjZmM0ODRhYjIzZjk3OGFlYmM3OSIsInN1YiI6IjY2NGNiY2QzOThmNWZjNWJiNTU1MzBhNCIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.sFJvo3zcsYMRMFGJKNkpUUJXYS1tdwvJ7zFWLlf8ZpY",
      },
    };

    fetch(
      "https://api.themoviedb.org/3/movie/popular?language=en-US&page=1",
      options
    )
      .then((response) => response.json())
      .then((response) => console.log(response))
      .catch((err) => console.error(err));
  }, []);

  function handleDatabase(movie) {
    const movieContainer = document.createElement("div");
    movieContainer.classList.add("movie-container");

    const divForImg = document.createElement("div");
    divForImg.classList.add("divForImg");
    console.log(
      "Title:",
      movie.results[0].original_title,
      ", IMG_POSTER:",
      movie.results[0].poster_path
    );

    const imgAdd = document.createElement("img");
    imgAdd.id = "grid-image";
    imgAdd.src = movie.results[0].poster_path;
    imgAdd.alt = movie.results[0].original_title;

    divForImg.append(imgAdd);
    movieContainer.append(divForImg);
  }
  return <div className='grid-container'>{handleDatabase}</div>;
}

export default ImagesPlace;
